import axios from 'axios';
import { Recipe, Ingredient } from '../schemas/recipe.schema';
// eslint-disable-next-line @typescript-eslint/no-var-requires
const cheerio = require('cheerio');

// TODO: Create tests
export async function getRecipe(
  url: string,
  testRun?: boolean,
): Promise<Recipe | string> {
  if (testRun) {
    console.log('Test run');
    return parseHtml(testHtml, url);
  }

  return axios
    .get(url)
    .then((response) => {
      const html = response.data;

      return parseHtml(html, url);
    })
    .catch(() => {
      return 'Failed';
    });
}

function parseHtml(html: string, url: string): Recipe {
  const $ = cheerio.load(html);

  const title = $('.recipe-header__title').text();
  const description = $('p', '.recipe-header__preamble').text();
  const minutesToMakeText = $('.prefix', '.cooking-steps').text();
  const minutesToMake = parseInt(minutesToMakeText.replace(/\D/g, ''));
  const servingsText = $('div', '.ingredients-change-portions').text();
  const servings = parseInt(servingsText.replace(/\D/g, ''));
  const imageLink = $('.recipe-header__image').attr('src');

  const ingredients: Ingredient[] = [];
  $(
    '.ingredients-list-group__card',
    '.ingredients-list-group.row-noGutter-column',
  ).each(function (i) {
    const quantityAndUnitLabel = $('.ingredients-list-group__card__qty', this)
      .text()
      .trim();
    const name = $('.ingredients-list-group__card__ingr', this).text().trim();
    ingredients[i] = {
      name,
      quantityAndUnitLabel,
      quantity: parseIngredientQuantity(quantityAndUnitLabel),
      unit: findUnit(quantityAndUnitLabel),
    };
  });

  const cookingSteps = [];
  $('.cooking-steps-main__text').each(function (i) {
    cookingSteps[i] = $(this).text();
  });

  const recipe = new Recipe();
  recipe.name = title;
  recipe.link = url;
  recipe.imageLink = imageLink;
  recipe.description = description;
  recipe.minutesToMake = minutesToMake;
  recipe.servings = servings;
  recipe.cookingSteps = cookingSteps;
  recipe.ingredients = ingredients;
  return recipe;
}

function parseIngredientQuantity(quantityAndUnitLabel: string): number | null {
  if (quantityAndUnitLabel === '' || quantityAndUnitLabel === '-') {
    return null;
  }
  const labelInParts = quantityAndUnitLabel.split(' ');
  function getNumberOrZero(arg: string): number {
    try {
      const evalResult = eval(arg);
      return isNaN(evalResult) ? (evalResult as number) : evalResult;
    } catch (e) {
      return 0;
    }
  }
  const numbersInLabel = labelInParts.map((part: string) => {
    if (units.includes(part) || part === '-') {
      return 0;
    }
    return getNumberOrZero(part);
  });
  return numbersInLabel.reduce((num, sum) => num + sum, 0);
}

function findUnit(quantityAndUnitLabel: string): string | null {
  if (quantityAndUnitLabel === '' || quantityAndUnitLabel === '-') {
    return null;
  }
  const labelInParts = quantityAndUnitLabel.split(' ');
  let unitResult = null;
  labelInParts.forEach((part) => {
    const foundUnit = units.find((u: string) => u === part);
    if (foundUnit) {
      unitResult = foundUnit;
    }
  });
  return unitResult;
}

